import { useMount } from 'react-use'
import { Routes, Route, useLocation } from 'react-router-dom'
import styles from './routes.module.scss'

import { useAppSelector, useEffect, useGA } from 'hooks'
import { getTheme } from 'states/system'

import LNB from 'routes/_shared/LNB'
import Header from 'routes/_shared/Header'
import Corona from './Corona'

const App = () => {
  const theme = useAppSelector(getTheme)
  const { initializeGA, gaPV } = useGA()
  const { pathname, search } = useLocation()

  useMount(() => {
    initializeGA()
    document.documentElement.setAttribute('color-theme', theme)
  })

  useEffect(() => {
    gaPV(`${pathname}${search}`)
  }, [gaPV, pathname, search])

  return (
    <div className={styles.appWrapper}>
      <LNB />
      <Header />
      <div className={styles.app}>
        <Routes>
          <Route path='/' element={<Corona />} />
          <Route path='*' element={<div>404</div>} />
        </Routes>
      </div>
    </div>
  )
}

export default App
